import React from 'react'
import { View, Text, StyleSheet, SafeAreaView, FlatList, ImageBackground, ScrollView} from 'react-native';
import { IconButton, Colors } from 'react-native-paper';


const photo={uri:'https://www.cocina-ecuatoriana.com/base/stock/Recipe/3-image/3-image_web.jpg'};

const Ingredientes=[
    {
        
        id:'1',
        n:'2 ½ tazas de almidón de yuca'
    },
    {
        id:'2',
        n:'4 tazas de queso rallado'
    },
    {
        id:'3',
        n:'1 cucharadita de polvo de hornear'
    },
    {
        id:'4',
        n:'Una pizca de sal'
    },
    {
        id:'5',
        n:'4 onzas de mantequilla'
    },
]
function Item (props){
    return(
        <View>
        <Text style={styles.description}>{props.item.n}</Text>
    </View>
    )
   
}


export default function Description({navigation}) {
    return (
        <View style={styles.container}>
            <View style={styles.ContentImage}>
                <ImageBackground style={styles.image} source={ photo}>
                    <View style={{flex: 6}}></View>
                    <View style={{flex: 3, alignItems: 'flex-end', padding: 5}}>    
                        <IconButton icon="comment" color={Colors.white} size={35} style={styles.review}
                            onPress={() => navigation.navigate('Reseñas')}/>             
                    </View> 
                </ImageBackground> 
            </View>
            <View style={styles.content}>
                <ScrollView>
                <Text style={styles.title}>Ingredientes:</Text>
                <SafeAreaView>
                    <FlatList
                        data={Ingredientes}
                        renderItem={({ item }) => <Item item={item} />}
                        keyExtractor={item => item.id}
                    />
                </SafeAreaView>
                <Text style={styles.title}>Preparación:</Text>
                <Text style={styles.description}>Pieza de panadería originaria de Ecuador ​El pandeyuca o pan de yuca es un panecillo, que es elaborado con queso blanco molido, almidón de yuca cernido, huevos, polvo de hornear y además sal; se forma en pequeñas porciones.</Text>
                </ScrollView>
            </View>
            
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1
        
    },
    ContentImage:{
        flex: 3.1,

    },
    content: {
        flex: 5,
        backgroundColor:'green'
    },
    item: {
        paddingBottom: 15
    },
    title: {
        fontSize: 22,
        marginLeft:10, 
        marginTop:5,
        fontWeight:'bold'
    },
    description: {
        fontSize: 15,
        marginLeft:10,
        marginTop:5,
        
    },
    image: {
        justifyContent: "center",
        width: '100%',
        height: 200
    },
    review: {
        backgroundColor: '#FC5B27'
    }
})
