export default function Review() {
    const [recetas, setRecetas] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        fetch('https://firestore.googleapis.com/v1/projects/recetas-app-31a18/databases/(default)/documents/reviews/')
        .then(response => response.json())
        .then(data => {
            setRecetas(data)
            setLoading(false)
            console.log(recetas)
        })
    }, [])
    if (loading) {
        return <View><Text>Cargando..</Text></View>
    }
    return (
        <View style={styles.container}>
            <View style={styles.content}>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <View style={styles.review}>
                    <Text style={styles.user}>Jhon</Text>
                    <TextInput style={styles.add} name="comentarios" multiline={true} numberOfLines={1} placeholder="Escribe aquí tus comentarios" />
                    <View style={styles.btn}>
                        <Button onPress={() => console.log('enviado')} color="#FC5B27" title='Enviar'></Button>
                    </View>
                </View>

            </View>

            <SafeAreaView style={styles.item}>
                 <FlatList
                    data={data}
                    renderItem={({ item }) =>  <Text>{item.review}</Text>}
                    keyExtractor={item => item.id}
                />
            </SafeAreaView>
            {/* <Item item={item} */}
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20
    },
    content: {
        paddingBottom: 10,
        paddingTop: 10,
        flexDirection: 'row',
    },

    user: {
