import React from 'react'
import { View, Text, StyleSheet, SafeAreaView, FlatList, ImageBackground} from 'react-native';



const DataRecipes = [
    {
        id: "1",
        title: "Pan de Yuca",
        photo: "https://www.cocina-ecuatoriana.com/base/stock/Recipe/3-image/3-image_web.jpg",
        reviews: "Comentarios de la receta",
        ingredientes:"Harina",
        preparacion: "Combine la harina o almidón de yuca, el queso, el polvo de hornear y la sal en el procesador de alimentos, mezcle bien",  
        
    }

]

function Item(props) {
    return (
        <View style={styles.item}>
            <ImageBackground style={styles.image} source={{ uri: props.item.photo }}>
            <View style={{flex: 6}}></View>
            <View style={{flex: 3, alignItems: 'flex-end', padding: 5}}>               
            </View> 
            </ImageBackground> 
            <Text style={{marginLeft:10, marginTop:5, marginBottom:6, fontWeight:'bold', fontSize:25 }} >{props.item.ingredientes}</Text> 
               
        </View>
    );
}

export default function Description(props) {
    return (
        <View style={styles.container}>
            <View style={styles.header}>
            
            </View>
            <View style={styles.content}>
                <SafeAreaView>
                    <FlatList
                        data={DataRecipes}
                        renderItem={({ item }) => <Item item={item} />}
                        keyExtractor={item => item.id}
                    />
                </SafeAreaView>
            </View>
            
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1
        
    },
    header: {
        flex: 1,
        backgroundColor: '#FC5B27',
        alignItems: 'center',
        paddingTop: 10,
        justifyContent:'center'
    },
    logo: {
        justifyContent: "center",
        width: '50%',
        height: '100%'
    },
    content: {
        flex: 7,
        padding: 20
    },
    item: {
        paddingBottom: 15
    },
    title: {
        fontSize: 25,
        marginLeft:10, 
        marginTop:5
    },
    image: {
        justifyContent: "center",
        width: '100%',
        height: 200
    },
    review: {
        backgroundColor: '#FC5B27'
    }
})
