import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, Button} from 'react-native';

const DataReiew =[
    {
        id:"1",
        photo:"",
        name:"",
        reseña:""
    },
    {   
        id:"2",
        photo:"",
        name:"",
        reseña:""
    },
    {   
        id:"3",
        photo:"",
        name:"",
        reseña:""
    },
    {   
        id:"4",
        photo:"",
        name:"",
        reseña:""
    },
    {   
        id:"5",
        photo:"",
        name:"",
        reseña:""
    }
]

function Item(props) {
    return (
        <View onPress={() => props.navigation.navigate('Descripción Receta')} style={styles.item}>
            <Text style={styles.title} >{props.item.title}</Text>
            <ImageBackground style={styles.image} source={{ uri: props.item.photo }}>
                <View style={{ flex: 6 }}></View>
                <View style={{ flex: 3, alignItems: 'flex-end', padding: 5 }}>
                    <IconButton icon="comment" color={Colors.white} size={35} style={styles.review}
                        onPress={() => props.navigation.navigate('Reseñas')} />
                </View>
            </ImageBackground>
        </View>
    )
}
export default function Review() {
    return (
        <View style={styles.container}>
            <View>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Jhon</Text>
                <TextInput name="comentarios" multiline={true}  numberOfLines={4} placeholder="Escribe aquí tus comentarios" />
                <Button title='Enviar'></Button>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario1.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Pablo</Text>
                <Text style={styles.comentario}>muy facil</Text >
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Julio</Text>
                <Text style={styles.comentario}>Falta explicacion</Text >
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario1.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>María</Text>
                <Text style={styles.comentario}>no se entiende</Text >
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20
    },
    user: {
        flex: 1,
        fontSize: 23,
        fontWeight: "bold",

    },
    image: {
        width: 50,
        height: 50,
        borderRadius: 360,

    },
    comentario: {
        flex: 1,
        fontSize: 23,
    }


})
