import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, Button, SafeAreaView, FlatList } from 'react-native';


const DataReview = [
    {
        id: "1",
        photo: 'usuario.jpg',
        name: "Carlos",
        reseña: "Muy facil"
    },
    {
        id: "2",
        photo: 'usuario.jpg',
        name: "Juan",
        reseña: "Falta Detalles"
    },
    {
        id: "3",
        photo: 'usuario.jpg',
        name: "Mario",
        reseña: "Exelente explicacion"
    },
    {
        id: "4",
        photo: 'usuario1.jpg',
        name: "Camila",
        reseña: "No se entiende"
    },
    {
        id: "5",
        photo: 'usuario1.jpg',
        name: "Fernanda",
        reseña: "Falta mas informacion"
    }
]

function Item(props) {
    
    return (
        <View style={styles.item}>
            <Image
                style={styles.image}
                source={require('../../assets/'+props.item.photo)}
                resizeMode='contain'
            />
            <View style={styles.review}>
                <Text style={styles.user}>{props.item.name}</Text>
                <Text style={styles.comentario}>{props.item.reseña}</Text >
            </View>
        </View>
    )
}
export default function Review() {
    return (
        <View style={styles.container}>
            <View>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Jhon</Text>
                <TextInput name="comentarios" multiline={true} numberOfLines={2} placeholder="Escribe aquí tus comentarios" />
                <Button style={styles.btn} title='Enviar'></Button>
                <SafeAreaView>
                    <FlatList
                        data={DataReview}
                        renderItem={({ item }) => <Item item={item} />}
                        keyExtractor={item => item.id}
                    />
                </SafeAreaView>
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20
    },
    item: {
        flexDirection: 'row'
    },
    user: {
        flex: 1,
        fontSize: 20,
        fontWeight: "bold",

    },
    image: {
        flex:1,
        width: 50,
        height: 50,
        borderRadius:160,

    },
    comentario: {
        flex: 1,
        fontSize: 18,
        backgroundColor: '#FC5B27',
        opacity:0.3
    },
    review:{
        flex:5
    },
    btn:{
        width: 10,
        

    }


})
