const [recetas,setRecetas]=useState([])

const [loading,setLoading]=useState(true)

useEffect(() => {
    fetch('https://firestore.googleapis.com/v1/projects/recetas-app-31a18/databases/(default)/documents/recetas/')
    .then(response => response.json())
    .then(data =>{
        setRecetas(data)
        setLoading(false)
    })
}, [])



function Item(props) {
    return (
        <TouchableOpacity onPress={() => props.navigation.navigate('Descripción Receta')} style={styles.item}>
            <Text style={styles.title} >{props.item.title}</Text>
            <ImageBackground style={styles.image} source={{ uri: props.item.photo }}>
                <View style={{ flex: 6 }}></View>
                <View style={{ flex: 3, alignItems: 'flex-end', padding: 5 }}>
                    <IconButton icon="comment" color={Colors.white} size={35} style={styles.review}
                        onPress={() => props.navigation.navigate('Reseñas')} />
                </View>
            </ImageBackground>
        </TouchableOpacity>
    )
}
export default function Recipes({ navigation }) {
    return (
        <View style={styles.container}>
            <Search />
            <View style={styles.content}>
                <SafeAreaView>
                    <FlatList
                        data={DataRecipes}
                        renderItem={({ item }) => <Item item={item} navigation={navigation} />}
                        keyExtractor={item => item.id}
                    />
                </SafeAreaView>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    content: {
        padding: 20
    },
    item: {
        paddingBottom: 15
    },
    title: {
        fontSize: 30,
