import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, Button} from 'react-native';

const DataReiew =[
    {
        id:"1",
        photo:"../../assets/usuario.jpg",
        name:"Carlos",
        reseña:""
    },
    {   
        id:"2",
        photo:"../../assets/usuario.jpg",
        name:"Juan",
        reseña:""
    },
    {   
        id:"3",
        photo:"../../assets/usuario.jpg",
        name:"Mario",
        reseña:""
    },
    {   
        id:"4",
        photo:"../../assets/usuario1.jpg",
        name:"Camila",
        reseña:""
    },
    {   
        id:"5",
        photo:"../../assets/usuario1.jpg",
        name:"Fernanda",
        reseña:""
    }
]

function Item(props) {
    return (
        <View style={styles.item}>
            <Image
                    style={styles.image}
                    source={require}
                    resizeMode='contain'
            />
                <Text style={styles.user}>{{}}</Text>
                <Text style={styles.comentario}>{{}}</Text >
        </View>
    )
}
export default function Review() {
    return (
        <View style={styles.container}>
            <View>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Jhon</Text>
                <TextInput name="comentarios" multiline={true}  numberOfLines={4} placeholder="Escribe aquí tus comentarios" />
                <Button title='Enviar'></Button>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario1.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Pablo</Text>
                <Text style={styles.comentario}>muy facil</Text >
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Julio</Text>
                <Text style={styles.comentario}>Falta explicacion</Text >
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario1.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>María</Text>
                <Text style={styles.comentario}>no se entiende</Text >
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20
    },
    item:{
        flexDirection:'row'
    },
    user: {
        flex: 1,
        fontSize: 23,
        fontWeight: "bold",

    },
    image: {
        width: 50,
        height: 50,
        borderRadius: 360,

    },
    comentario: {
        flex: 1,
        fontSize: 23,
    }


})
