import React from 'react';
import { View, Text, StyleSheet, Image, TextInput, Button} from 'react-native';

const DataReview =[
    {
        id:"1",
        photo:"../../assets/usuario.jpg",
        name:"Carlos",
        reseña:"Muy facil"
    },
    {   
        id:"2",
        photo:"../../assets/usuario.jpg",
        name:"Juan",
        reseña:"Falta Detalles"
    },
    {   
        id:"3",
        photo:"../../assets/usuario.jpg",
        name:"Mario",
        reseña:"Exelente explicacion"
    },
    {   
        id:"4",
        photo:"../../assets/usuario1.jpg",
        name:"Camila",
        reseña:"No se entiende"
    },
    {   
        id:"5",
        photo:"../../assets/usuario1.jpg",
        name:"Fernanda",
        reseña:"Falta mas informacion"
    }
]

function Item(props) {
    return (
        <View style={styles.item}>
            <Image
                    style={styles.image}
                    source={{require:props.item.photo}}
                    resizeMode='contain'
            />
                <Text style={styles.user}>{props.item.name}</Text>
                <Text style={styles.comentario}>{props.item.reseña}</Text >
        </View>
    )
}
export default function Review() {
    return (
        <View style={styles.container}>
            <View>
                <Image
                    style={styles.image}
                    source={require('../../assets/usuario.jpg')}
                    resizeMode='contain'
                />
                <Text style={styles.user}>Jhon</Text>
                <TextInput name="comentarios" multiline={true}  numberOfLines={4} placeholder="Escribe aquí tus comentarios" />
                <Button title='Enviar'></Button>
                <SafeAreaView>
                    <FlatList
                        data={DataReview}
                        renderItem={({ item }) => <Item item={item} navigation={navigation} />}
                        keyExtractor={item => item.id}
                    />
                </SafeAreaView>
            </View>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20
    },
    item:{
        flexDirection:'row'
    },
    user: {
        flex: 1,
        fontSize: 23,
        fontWeight: "bold",

    },
    image: {
        width: 50,
        height: 50,
        borderRadius: 360,

    },
    comentario: {
        flex: 1,
        fontSize: 23,
    }


})
