import React from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import { Input } from 'react-native-elements';
import AuthContext from './context';


export default function LoginScreen() {
    
    const a = 3
    const [password, setPassword] = useState('')
    const [correo, setCorreo] = useState('')
    const [check, setCheck] = useState(false)
    const logind = firebase.firestore().collection('login');
    const [data, setData] = useState([]);
    const [one, setOne] = useState();
    

    
    const oneDocument = () => {

        firebase.firestore().collection('login').where('email', '==', correo).get()
            .then((snapshot) => {
                if(snapshot.size==0){
                    Alert.alert('No valido')
                }
                snapshot.forEach((doc) => {
                   let item=doc.data()
                   if(item.password==password){
                    console.log("login");
                    navigation.navigate('Content')
                   }else{
                    console.log("error");
                   }
                })
            }
            )
            .catch((err) => {
                console.log('Error getting documents', err);
            });
        firebase.firestore().collection('login').doc('1').get()
            .then(snapshot => {
                setOne(snapshot.data())
            })
            .catch((err) => {
                console.log('Error getting documents', err);
            })
    }


        

    return (
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
            <Text>Home Screen</Text>
            <Input
                placeholder='BASIC INPUT'
            />
            <TouchableOpacity onPress={login}><Text>Login</Text></TouchableOpacity>
        </View>
    );
}
